describe('useIsSmallScreen', () => {
  const originalMatchMedia = window.matchMedia;

  beforeEach(() => {
    console.log('[SETUP] useIsSmallScreen test starting');
  });

  afterEach(() => {
    window.matchMedia = originalMatchMedia;
  });

  function mockMatchMedia(matches: boolean) {
    const listeners: Array<(e: MediaQueryListEvent) => void> = [];

    window.matchMedia = jest.fn().mockImplementation((query: string) => ({
      matches,
      media: query,
      onchange: null,
      addListener: jest.fn((cb) => listeners.push(cb)),
      removeListener: jest.fn(),
      addEventListener: jest.fn((_, cb) => listeners.push(cb)),
      removeEventListener: jest.fn(),
      dispatchEvent: jest.fn(),
    }));

    return {
      triggerChange: (newMatches: boolean) => {
        listeners.forEach(cb => cb({ matches: newMatches } as MediaQueryListEvent));
      }
    };
  }

  test('returns true for mobile viewport', () => {
    mockMatchMedia(true);

    const { result } = renderHook(() => useIsSmallScreen());

    expect(result.current).toBe(true);
    console.log('[PASS] Returns true for mobile viewport');
  });

  test('returns false for desktop viewport', () => {
    mockMatchMedia(false);

    const { result } = renderHook(() => useIsSmallScreen());

    expect(result.current).toBe(false);
    console.log('[PASS] Returns false for desktop viewport');
  });

  test('updates on resize', () => {
    const { triggerChange } = mockMatchMedia(false);

    const { result } = renderHook(() => useIsSmallScreen());

    expect(result.current).toBe(false);
    console.log('[CHECK] Initially desktop');

    act(() => {
      triggerChange(true);
    });

    expect(result.current).toBe(true);
