// apps/web/src/components/related-prompts.tsx

interface RelatedPromptsProps {
  promptId: string;
  limit?: number;
}

export function RelatedPrompts({ promptId, limit = 5 }: RelatedPromptsProps) {
  const related = useMemo(() => {
    const current = getPrompt(promptId);
    if (!current) return [];

    // Score by tag overlap + BM25 on title/description
    const candidates = prompts.filter(p => p.id !== promptId);
    const scored = candidates.map(p => ({
      prompt: p,
      score: calculateTagOverlap(current.tags, p.tags) + 
             bm25Score(current.title + ' ' + current.description, p)
    }));

    return scored
      .sort((a, b) => b.score - a.score)
      .slice(0, limit)
      .map(s => s.prompt);
  }, [promptId, limit]);

  if (related.length === 0) return null;

  return (
    <section>
      <h3>Related Prompts</h3>
      <div className='grid grid-cols-2 md:grid-cols-3 gap-4'>
        {related.map(p => (
          <PromptCard key={p.id} prompt={p} compact />
        ))}
      </div>
    </section>
  );
}
```

**Scoring Algorithm:**
- Tag overlap: +1 point per shared tag
- BM25: Score title+description against current prompt
- Weighted sum: 60% tags, 40% BM25

